-- RESEARCH DOMAINS

-- create table for RSE research domains
CREATE TABLE research_domain (
	id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
	key VARCHAR(50) UNIQUE NOT NULL,
	name VARCHAR(200),
	description VARCHAR(500),
	parent UUID REFERENCES research_domain (id)
);

CREATE FUNCTION check_cycle_research_domain() RETURNS TRIGGER STABLE LANGUAGE plpgsql SECURITY DEFINER AS
$$
DECLARE initial_rd UUID = NEW.id;
DECLARE current_rd UUID = NEW.parent;
BEGIN
	WHILE current_rd IS NOT NULL LOOP
		IF current_rd = initial_rd THEN
			RAISE EXCEPTION USING MESSAGE = 'Cycle detected for research domain with id ' || NEW.id;
		END IF;
		SELECT parent FROM research_domain WHERE id = current_rd INTO current_rd;
	END LOOP;
	RETURN NEW;
END
$$;

-- z_ prefix so that if is executed after the sanitise_update_organisation trigger
CREATE TRIGGER z_check_cycle_research_domain BEFORE UPDATE OF parent ON research_domain FOR EACH ROW EXECUTE PROCEDURE check_cycle_research_domain();


CREATE FUNCTION sanitise_insert_research_domain() RETURNS TRIGGER LANGUAGE plpgsql AS
$$
BEGIN
	NEW.id = gen_random_uuid();
	return NEW;
END
$$;

CREATE TRIGGER sanitise_insert_research_domain BEFORE INSERT ON research_domain FOR EACH ROW EXECUTE PROCEDURE sanitise_insert_research_domain();


CREATE FUNCTION sanitise_update_research_domain() RETURNS TRIGGER LANGUAGE plpgsql AS
$$
BEGIN
	NEW.id = OLD.id;
	return NEW;
END
$$;

CREATE TRIGGER sanitise_update_research_domain BEFORE UPDATE ON research_domain FOR EACH ROW EXECUTE PROCEDURE sanitise_update_research_domain();


-- create reference table for projects
CREATE TABLE research_domain_for_project (
	project UUID REFERENCES project (id),
	research_domain UUID REFERENCES research_domain (id),
	PRIMARY KEY (project, research_domain)
);


